DROP TABLE IF EXISTS waitlist; CREATE TABLE waitlist (id UUID DEFAULT gen_random_uuid() PRIMARY KEY, name TEXT NOT NULL, email TEXT NOT NULL UNIQUE, school TEXT NOT NULL, linkedin TEXT, created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL); ALTER TABLE waitlist ENABLE ROW LEVEL SECURITY; CREATE POLICY waitlist_insert_policy ON waitlist FOR INSERT TO anon WITH CHECK (true);
